  teamwork, physical_activity, creativity, driving, travel, hours_flexibility, care_work, danger) VALUES (407, 'Oceanographer','As an oceanographer, you''ll use science and mathematics to study and explain the complex interactions between seawater, fresh water, polar ice caps, the atmosphere and the biosphere.', 'Océanographe','En tant qu''océanographe, vous utiliserez les sciences et les mathématiques pour étudier et expliquer les interactions complexes entre l''eau de mer, les eaux douces, les calottes polaires, l''atmosphère et la biosphère.',9, 3, 4, 8, 1, 9,7, 8, 2);
INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
  teamwork, physical_activity, creativity, driving, travel, hours_flexibility, care_work, danger) VALUES (408, 'Offshore Drilling Worker','You will work in a variety of roles on oil or gas rigs. You could be working as a driller, assistant driller or a derrickman. You’ll be looking after the drill and other tools used to explore and extract gas and oil from a well.', 'Ouvrier / ouvrière au forage en mer','Vous travaillerez dans divers rôles sur des plates-formes pétrolières ou gazières. Vous pourriez travailler en tant que foreur, assistant foreur ou derrickman. Vous vous occuperez de la perceuse et des autres outils utilisés pour explorer et extraire le gaz et le pétrole d’un puits.',9, 2, 1, 7, 2, 9,8, 8, 2);
INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
