INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
  teamwork, physical_activity, creativity, driving, travel, hours_flexibility, care_work, danger) VALUES (412, 'Countryside Ranger','Your job will be to protect, manage and enhance the habitats of wildlife. You will be working in an area open to the public such as national parks and ensure that people can use, enjoy and understand the countryside without disturbing plants or animals.', 'Ranger de campagne','Votre travail consistera à protéger, gérer et améliorer les habitats de la faune. Vous travaillerez dans une zone ouverte au public, telle que les parcs nationaux, et vous veillerez à ce que les visiteurs puissent utiliser, apprécier et comprendre la campagne sans déranger les plantes ou les animaux.',9, 4, 2, 5, 2, 9,7, 3, 7);
INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
  teamwork, physical_activity, creativity, driving, travel, hours_flexibility, care_work, danger) VALUES (413, 'Remote Sensing Technician','Remote Sensing Technicians apply remote sensing technologies to assist scientists in areas such as natural resources, urban planning, or homeland security.', 'Technicien en télédétection','Les techniciens en télédétection appliquent des technologies de télédétection pour aider les scientifiques dans des domaines tels que les ressources naturelles, la planification urbaine ou la sécurité intérieure.',9, 4, 6, 8, 1, 5,4, 8, 6);
