INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
  teamwork, physical_activity, creativity, driving, travel, hours_flexibility, care_work, danger) VALUES (432, 'Air Traffic Controller','As an air traffic controller you will need to give landing and taking off instructions to pilots, monitor and direct the movement of the aircrafts on the ground and in the air using radar, computer and visual references. You are also be in charge of all ground traffic at a airport including baggage vehicles and airport workers.', 'Contrôleur / Contrôleuse de Circulation Aérienne','Dans le rôle du contrôleur de la circulation aérienne, vous devrez donner des instructions d''atterrissage et de décollage aux pilotes, surveiller et diriger les mouvements des aéronefs au sol et dans l''air en utilisant des références radar, informatiques et visuelles. Vous êtes également responsable de tout le trafic au sol d''un aéroport, y compris les véhicules à bagages et le personnel de l''aéroport.',10, 3, 8, 8, 1, 6,5, 9, 4);
INSERT INTO `pontoonapps_workfindr2`.`jobs` (id, title_en, description_en, titre_fr, description_fr, category_id,
